package week2.day2;

public class PositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n = -40;

if(n<0)
{
	n=n*-1;
	System.out.println("the number is converted to positive: " +n);
}
else
	System.out.println("given number is positive: " +n);
	
	}

}
